sql.sql:
WITH cols AS ( SELECT n.nspname AS schemaname, c.relname AS tablename, a.attnum, a.attname, format_type(a.atttypid, a.atttypmod) AS formatted_type, t.typalign, t.typlen, a.attnotnull, a.attidentity, a.attgenerated, ad.adbin FROM pg_class c JOIN pg_namespace n ON n.oid = c.relnamespace JOIN pg_attribute a ON a.attrelid = c.oid JOIN pg_type t ON t.oid = a.atttypid LEFT JOIN pg_attrdef ad ON ad.adrelid = a.attrelid AND ad.adnum = a.attnum WHERE n.nspname = '$1' AND c.relname = '$2' AND a.attnum > 0 AND NOT a.attisdropped ) SELECT 'CREATE TABLE ' || format('%I.%I_tetris', schemaname, tablename) || '
(' || string_agg('  ' || quote_ident(attname) || ' ' || formatted_type || CASE attidentity WHEN 'a' THEN ' GENERATED ALWAYS AS IDENTITY' WHEN 'd' THEN ' GENERATED BY DEFAULT AS IDENTITY' ELSE '' END || CASE WHEN attgenerated = 's' THEN ' GENERATED ALWAYS AS (' || pg_get_expr(adbin, format('%I.%I', schemaname, tablename)::regclass) || ') STORED' WHEN adbin IS NOT NULL THEN ' DEFAULT ' || pg_get_expr(adbin, format('%I.%I', schemaname, tablename)::regclass) ELSE '' END || CASE WHEN attnotnull THEN ' NOT NULL' ELSE '' END, ',
' ORDER BY CASE WHEN typlen < 0 THEN 1 ELSE 0 END ASC, CASE typalign WHEN 'd' THEN 4 WHEN 'i' THEN 3 WHEN 's' THEN 2 WHEN 'c' THEN 1 ELSE 0 END DESC, CASE WHEN typlen < 0 THEN NULL ELSE typlen END DESC, attnum ) || '
);' AS create_table_tetris_ddl FROM cols GROUP BY schemaname, tablename;
